import React, { useState, useEffect, useCallback } from 'react';

function NavBar() {
    const [isFormOpen, setIsFormOpen] = useState(false);

    const handleButtonClick = () => {
        setIsFormOpen(!isFormOpen);
    };

    const handleOutsideClick = useCallback((event) => {
        if (isFormOpen && !event.target.closest('.login-form')) {
            setIsFormOpen(false);
        }
    }, [isFormOpen]);

    useEffect(() => {
        document.addEventListener('click', handleOutsideClick);
        return () => {
            document.removeEventListener('click', handleOutsideClick);
        };
    }, [handleOutsideClick]);

    return (
        <nav>
            <div className="logo">
                <a href="/" aria-label="Home"> MangaKu </a>
            </div>
            <ul>
                <li><a href="/" aria-label="Home">Home</a></li>
                <li><a href="#" aria-label="Lista">Lista</a></li>
                <li>
                    <button className="loginbtn" id="toggleButton" style={{ width: 'auto' }} onClick={handleButtonClick}>
                        Cadastro/Login
                    </button>
                </li>
            </ul>
            <div className="menu-icon">
                <img src={'/assets/icon.png'} alt="" />
            </div>
            {isFormOpen && (
                <div className="login-form">
                    {/* Adicione o seu formul√°rio de login/cadastro aqui */}
                    {/* Exemplo: */}
                    {/* <LoginForm /> */}
                </div>
            )}
        </nav>
    );
}

export default NavBar;
