// LoginForm.js
import React, { Component } from 'react';

class LoginForm extends Component {
    constructor() {
        super();
        this.state = {
            loginFormVisible: false,
            isRegister: false,
        };
        this.formRef = React.createRef(); // Ref para o formulário
    }

    componentDidMount() {
        document.addEventListener('mousedown', this.handleClickOutside);
    }

    componentWillUnmount() {
        document.removeEventListener('mousedown', this.handleClickOutside);
    }

    setWrapperRef = (node) => {
        this.formRef = node;
    };

    handleClickOutside = (event) => {
        if (this.formRef && !this.formRef.contains(event.target)) {
            // Clique fora do formulário, oculte o formulário
            this.setState({
                loginFormVisible: false,
                isRegister: false,
            });
        }
    };

    toggleForm = () => {
        this.setState((prevState) => ({
            loginFormVisible: !prevState.loginFormVisible,
            isRegister: false,
        }));
    }

    switchToRegister = () => {
        this.setState({
            isRegister: true,
        });
    }

    login = (event) => {
        event.preventDefault();
        // Código da função login
        // Aqui você pode adicionar a lógica de login se necessário
    }

    register = (event) => {
        event.preventDefault();
        // Código da função register
        // Aqui você pode adicionar a lógica de registro se necessário
    }

    render() {
        const { loginFormVisible, isRegister } = this.state;

        return (
            <div className={`form-container ${loginFormVisible ? 'visible' : ''}`} ref={this.setWrapperRef}>
                <div className="form-box">
                    <div className='button-box'>
                        <div id='btn'></div>
                        <button type='button' onClick={this.toggleForm} className='toggle-btn'>{isRegister ? 'Login' : 'Register'}</button>
                        <button type='button' onClick={this.switchToRegister} className='toggle-btn'>{isRegister ? 'Switch to Login' : 'Switch to Register'}</button>
                    </div>

                    {/* Formulário de Login e Registro unificado */}
                    <form className={isRegister ? 'input-group-register' : 'input-group-login'} onSubmit={isRegister ? this.register : this.login}>
                        {/* ... restante do formulário ... */}
                        <button type='submit' className='submit-btn'>{isRegister ? 'Cadastro' : 'Login'}</button>
                    </form>
                </div>
            </div>
        );
    }
}

export default LoginForm;
