import React, { useState } from 'react';
import '../components/NavBarcss'; // Substitua 'seu-estilo.css' pelo nome real do seu arquivo CSS

const NavBar = () => {
    const [loginFormVisible, setLoginFormVisible] = useState(false);

    const toggleForm = () => {
        setLoginFormVisible(!loginFormVisible);
    };

    const closeForm = (event) => {
        if (!document.getElementById('login-form').contains(event.target) && event.target.id !== 'toggleButton') {
            setLoginFormVisible(false);
        }
    };

    return (
        <nav>
            <div className="logo">
                <a href="Index.html"> AnimaKu </a>
            </div>
            <ul>
                <li><a href="Index.html">Home</a></li>
                <li><a href="#">Lista</a></li>
                <li><button className='loginbtn' id="toggleButton" style={{ width: 'auto' }} onClick={toggleForm}>Cadastro/Login</button></li>
            </ul>
            <div className="menu-icon">
                <img src="imagens/lista-de-reproducao.png" alt="" />
            </div>

            {/* Formulário de Login/Registro */}
            {loginFormVisible && (
                <div id='login-form' className='login-page' onClick={closeForm}>
                    <div className="form-box">
                        {/* Conteúdo do formulário aqui */}
                    </div>
                </div>
            )}
        </nav>
    );
};

export default NavBar;
