// Navbar.js
import React, { useState } from 'react';
import { HiHomeModern } from 'react-icons/hi2';
import { FaListAlt } from 'react-icons/fa';
import logo from '../assets/logo.jpg';
import LoginForm from './LoginForm'; // Importe o componente do formulário
import './Navbar.css';

function Navbar() {
  const [isLoginFormVisible, setIsLoginFormVisible] = useState(false);

  const toggleForm = () => {
    setIsLoginFormVisible(!isLoginFormVisible);
  };

  return (
    <div className="navbar-container">
      <a className="navbar-logo">
        <img className="logo" src={logo} alt="Logo" />
        <div className="link-text">MangaKu</div>
      </a>
      <ul className="nav-menu">
        <li className="nav-item">
          <a className="nav-link" href="#home"> {/* Adicione um ID ou referência apropriada aqui */}
            <HiHomeModern className="nav-icon" />
            <div className="link-text">HOME</div>
          </a>
        </li>
        <li className="nav-item">
          <a className="nav-link" href="#listagem"> {/* Adicione um ID ou referência apropriada aqui */}
            <FaListAlt className="nav-icon" />
            <div className="link-text">LISTAGEM</div>
          </a>
        </li>
        <li className="nav-item">
          <a className="nav-link" onClick={toggleForm} href="#login-cadastro"> {/* Adicione um ID ou referência apropriada aqui */}
            <button className="loginbtn" id="toggleButton" style={{ width: 'auto' }}>
              Login - Cadastro
            </button>
            <div className="link-text">Login/Cadastro</div>
          </a>
        </li>
      </ul>

      {/* Renderiza o formulário quando isLoginFormVisible for verdadeiro */}
      {isLoginFormVisible && <LoginForm onClose={() => setIsLoginFormVisible(false)} />}
    </div>
  );
}

export default Navbar;
