// App.jsx
import React, { useState } from 'react';
import { useRoutes, Route, HashRouter } from 'react-router-dom';
import BgVideo from './components/BgVideo';
import Navbar from './components/Navbar';
import LoginForm from './components/LoginForm';
import Listagem from './pages/Listagem';
import Home from './pages/Home';

function App() {
  const [isLoginFormVisible, setIsLoginFormVisible] = useState(false);
  const [userData, setUserData] = useState(null);

  const handleOpenLoginForm = () => {
    setIsLoginFormVisible(true);
  };

  const handleCloseLoginForm = () => {
    setIsLoginFormVisible(false);
  };

  const handleUserData = (data) => {
    setUserData(data);
  };

  const routes = useRoutes([
    { path: '/', element: <Home /> },
    { path: '/listagem', element: <Listagem userData={userData} /> },
    // Adicione mais rotas conforme necessário
  ]);

  return (
    <div className="app">
      <BgVideo />
      <Navbar onOpenLoginForm={handleOpenLoginForm} />
      {isLoginFormVisible && <LoginForm onClose={handleCloseLoginForm} onUserData={handleUserData} />}
      <HashRouter>
        {routes}
      </HashRouter>
    </div>
  );
}

export default App;
