import React, { useState } from 'react';

function NavBar() {
    const [isFormOpen, setIsFormOpen] = useState(false);

    const handleButtonClick = () => {
        // Alterna entre abrir e fechar o formulário
        setIsFormOpen(!isFormOpen);
    };

    const handleOutsideClick = (event) => {
        // Fecha o formulário se clicar fora dele
        if (isFormOpen && !event.target.closest('.login-form')) {
            setIsFormOpen(false);
        }
    };

    // Adiciona um ouvinte de evento para fechar o formulário ao clicar fora dele
    React.useEffect(() => {
        document.addEventListener('click', handleOutsideClick);
        return () => {
            document.removeEventListener('click', handleOutsideClick);
        };
    }, [isFormOpen]);

    return (
        <nav>
            {/* ... (seu código existente) */}
            <li><button className="loginbtn" id="toggleButton" style={{ width: 'auto' }} onClick={handleButtonClick}>Cadastro/Login</button></li>
            
            {/* Renderiza o formulário apenas se estiver aberto */}
            {isFormOpen && (
                <div className="login-form">
                    {/* Adicione o seu formulário de login/cadastro aqui */}
                    {/* Exemplo: */}
                    {/* <LoginForm /> */}
                </div>
            )}
        </nav>
    );
}

export default NavBar;
