// LoginForm.js
import React, { useState } from 'react';

const LoginForm = ({ onClose }) => {
    const [isRegister, setIsRegister] = useState(false);

    const toggleForm = () => {
        onClose(); // Fecha o formulário ao clicar fora
    };

    const switchToRegister = () => {
        setIsRegister(!isRegister);
    };

    const login = (event) => {
        event.preventDefault();
        // Código da função login
        // Aqui você pode adicionar a lógica de login se necessário
        onClose(); // Fecha o formulário após o login
    };

    const register = (event) => {
        event.preventDefault();
        // Código da função register
        // Aqui você pode adicionar a lógica de registro se necessário
        onClose(); // Fecha o formulário após o registro
    };

    return (
        <div className={`form-container`}>
            <div className="form-box" onClick={toggleForm}>
                <div className='button-box'>
                    <div id='btn'></div>
                    <button type='button' className='toggle-btn' onClick={switchToRegister}>Log In</button>
                    <button type='button' className='toggle-btn' onClick={switchToRegister}>Register</button>
                </div>

                {/* Formulário de Login e Registro unificado */}
                <form className={`input-group ${isRegister ? 'register' : 'login'}`} onClick={(e) => e.stopPropagation()} onSubmit={isRegister ? register : login}>
                    {isRegister && (
                        <>
                            <input type='text' className='input-field' placeholder='First Name' required />
                            <input type='text' className='input-field' placeholder='Last Name ' required />
                            <input type='email' className='input-field' placeholder='Email Id' required />
                            <input type='password' className='input-field' placeholder='Enter Password' required />
                            <input type='password' className='input-field' placeholder='Confirm Password' required />
                            <input type='checkbox' className='check-box' /><span>I agree to the terms and conditions</span>
                            <button type='submit' className='submit-btn'>Cadastro</button>
                        </>
                    )}
                    {!isRegister && (
                        <>
                            <input type='text' className='input-field' placeholder='Email Id' required />
                            <input type='password' className='input-field' placeholder='Enter Password' required />
                            <input type='checkbox' className='check-box' /><span>Remember Password</span>
                            <button type='submit' className='submit-btn'>Login</button>
                        </>
                    )}
                </form>
            </div>
        </div>
    );
};

export default LoginForm;
