import React from 'react';
import { useDrag } from 'react-dnd';
import './Listagem.css';

const Banner = ({ children }) => {
  const [{ isDragging }, drag] = useDrag({
    type: 'BANNER',
  });

  return (
    <div ref={drag} className="banner" style={{ opacity: isDragging ? 0.5 : 1 }}>
      {children}
    </div>
  );
};

const Listagem = ({ usersData }) => {
  return (
    <div>
      {/* Banner arrast√°vel com a tabela */}
      <Banner>
        <h2>Quadro de Dados</h2>
        <table>
          <thead>
            <tr>
              <th>Nome</th>
              <th>Sobrenome</th>
              <th>Email</th>
            </tr>
          </thead>
          <tbody>
            {usersData && usersData.map((user, index) => (
              <tr key={index}>
                <td>{user.firstName}</td>
                <td>{user.lastName}</td>
                <td>{user.email}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </Banner>
    </div>
  );
};

export default Listagem;
