// App.js

import React, { useState } from 'react';
import BgVideo from './components/BgVideo';
import Navbar from './components/Navbar';
import LoginForm from './components/LoginForm';
import Listagem from './pages'; // Importe o componente Listagem

function App() {
  const [isLoginFormVisible, setIsLoginFormVisible] = useState(false);
  const [userData, setUserData] = useState(null);

  const handleOpenLoginForm = () => {
    setIsLoginFormVisible(true);
  };

  const handleCloseLoginForm = () => {
    setIsLoginFormVisible(false);
  };

  const handleUserData = (data) => {
    setUserData(data);
  };

  return (
    <div className="app">
      <BgVideo />
      <Navbar onOpenLoginForm={handleOpenLoginForm} />
      {isLoginFormVisible && <LoginForm onClose={handleCloseLoginForm} onUserData={handleUserData} />}
      {userData && <Listagem userData={userData} />} {/* Renderiza Listagem com dados do usu√°rio */}
    </div>
  );
}

export default App;
